import React, { useState, useEffect } from 'react';
import { Plus, Trash2, Search } from 'lucide-react';
import { adminService } from '../../services/adminService';

const AdminMatriculas = () => {
  const [matriculas, setMatriculas] = useState([]);
  const [alumnos, setAlumnos] = useState([]);
  const [asignaturas, setAsignaturas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [formData, setFormData] = useState({
    alumno_id: '',
    asignatura_id: ''
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [matriculasData, alumnosData, asignaturasData] = await Promise.all([
        adminService.getMatriculas(),
        adminService.getAlumnos(),
        adminService.getAsignaturas()
      ]);
      setMatriculas(matriculasData);
      setAlumnos(alumnosData);
      setAsignaturas(asignaturasData);
    } catch (error) {
      console.error('Error al cargar datos:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await adminService.createMatricula({
        alumno_id: parseInt(formData.alumno_id),
        asignatura_id: parseInt(formData.asignatura_id)
      });
      loadData();
      setShowModal(false);
      resetForm();
    } catch (error) {
      console.error('Error al crear matrícula:', error);
    }
  };

  const resetForm = () => {
    setFormData({
      alumno_id: '',
      asignatura_id: ''
    });
  };

  const handleDeleteMatricula = async (alumnoId, asignaturaId) => {
    try {
      await adminService.deleteMatricula(alumnoId, asignaturaId);
      loadData(); // Recargar los datos después de eliminar
    } catch (error) {
      console.error('Error al eliminar matrícula:', error);
      alert('Error al eliminar la matrícula. Inténtalo de nuevo.');
    }
  };

  const filteredMatriculas = matriculas.filter(matricula =>
    matricula.alumno?.nombre_completo.toLowerCase().includes(searchTerm.toLowerCase()) ||
    matricula.asignatura?.nombre.toLowerCase().includes(searchTerm.toLowerCase()) ||
    matricula.alumno?.dni.includes(searchTerm)
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Gestión de Matrículas</h1>
        <button
          onClick={() => setShowModal(true)}
          className="btn-primary flex items-center"
        >
          <Plus className="h-4 w-4 mr-2" />
          Nueva Matrícula
        </button>
      </div>

      {/* Búsqueda */}
      <div className="relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          placeholder="Buscar por alumno, asignatura o DNI..."
          className="input-field pl-10"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Tabla de matrículas */}
      <div className="card">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Alumno
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  DNI
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Asignatura
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Docente
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Acciones
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredMatriculas.map((matricula, index) => (
                <tr key={`${matricula.alumno_id}-${matricula.asignatura_id}`} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {matricula.alumno?.nombre_completo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {matricula.alumno?.dni}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {matricula.asignatura?.nombre}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {matricula.asignatura?.docente?.nombre_completo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button
                      onClick={() => {
                        if (window.confirm('¿Estás seguro de eliminar esta matrícula?')) {
                          // TODO: Implementar eliminación
                          console.log('Eliminar matrícula:', matricula);
                        }
                      }}
                      className="text-red-600 hover:text-red-900"
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal para crear matrícula */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Nueva Matrícula
              </h3>
              
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Alumno
                  </label>
                  <select
                    required
                    className="input-field"
                    value={formData.alumno_id}
                    onChange={(e) => setFormData({...formData, alumno_id: e.target.value})}
                  >
                    <option value="">Seleccionar alumno</option>
                    {alumnos.map((alumno) => (
                      <option key={alumno.id} value={alumno.id}>
                        {alumno.nombre_completo} - {alumno.dni}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Asignatura
                  </label>
                  <select
                    required
                    className="input-field"
                    value={formData.asignatura_id}
                    onChange={(e) => setFormData({...formData, asignatura_id: e.target.value})}
                  >
                    <option value="">Seleccionar asignatura</option>
                    {asignaturas.map((asignatura) => (
                      <option key={asignatura.id} value={asignatura.id}>
                        {asignatura.nombre} - {asignatura.docente?.nombre_completo}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      resetForm();
                    }}
                    className="btn-secondary"
                  >
                    Cancelar
                  </button>
                  <button type="submit" className="btn-primary">
                    Matricular
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminMatriculas;
