import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor para agregar el token a las peticiones
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    console.log('üîë Interceptor request - Token:', token ? 'Presente' : 'Ausente');
    console.log('üåê Interceptor request - URL:', config.url);
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('‚úÖ Token agregado a headers');
    } else {
      console.log('‚ùå No hay token disponible');
    }
    return config;
  },
  (error) => {
    console.error('‚ùå Error en interceptor request:', error);
    return Promise.reject(error);
  }
);

// Interceptor para manejar respuestas de error
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ Respuesta exitosa:', response.config.url, response.status);
    return response;
  },
  (error) => {
    console.error('‚ùå Error en respuesta:', error.config?.url, error.response?.status);
    console.error('‚ùå Detalles del error:', error.response?.data);
    if (error.response?.status === 401) {
      console.log('üîê Token expirado o inv√°lido, redirigiendo a login');
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
